function checkCatalogTasks() {
	var grTask = new GlideRecord('sn_niem_j_property_tasks');
	grTask.addQuery('property_id', current.sys_id);
	grTask.addQuery('active', true);
	grTask.query();

	if (grTask.hasNext()) {
		answer = false;
	} else {
		answer = true;
	}
}

